int N;
vector<int> G[100005];
int dp[100005];

int calc(int p, int x){
  if(dp[p] != -1) return dp[p];
  int ret = 0;
  vector<int> res;
  rep(i,G[p].size()){
    if(G[p][i] == x) continue;
    res.push_back(calc(G[p][i], p));
  }
  if(res.size()==0) ret = 0;
  else if(res.size()==1) ret = res[0] + 1;
  else{
    rep(i,res.size()){
      ret ^= (res[i]+1);
    }
  }
  return dp[p] = ret;
}

int main(){
  cin >> N;
  rep(i,N-1){
    int x, y;
    cin >> x >> y;
    G[x].push_back(y);
    G[y].push_back(x);
  }

  rep(i,100005){
    dp[i] = -1;
  }

  calc(1, 0);

  if(dp[1] != 0) cout << "Alice" << endl;
  else cout << "Bob" << endl;

  return 0;
}

