const int MAXN = 100005;
int dp[MAXN], visited[MAXN];

int n;

vector<vector<int>> adj;

void dfs(int u) {
    visited[u] = 1;

    dp[u] = 0;

    int children = 0;

    for (auto& v : adj[u]) {
        if (!visited[v]) {
            children++;
        }
    }

    if (children == 1) {
        for (auto& v : adj[u]) {
            if (!visited[v]) {
                dfs(v);
                dp[u] = dp[v] + 1;
            }
        }
    }
    else {
        for (auto& v : adj[u]) {
            if (!visited[v]) {
                dfs(v);
                dp[u] ^= dp[v];
            }
        }
    }
}

