template <class Cost = int>
struct Edge {
    int from, to;
    Cost cost;
    Edge(int from = -1, int to = -1, Cost cost = 1)
        : from(from), to(to), cost(cost){};

    bool operator<(const Edge<Cost>& e) const { return this->cost < e.cost; }
    bool operator>(const Edge<Cost>& e) const { return this->cost > e.cost; }
};

template <class Cost = int>
class Graph {
public:
    int size;
    std::vector<std::vector<Edge<Cost>>> path;

    explicit Graph(int N = 0) : size(N), path(size) {}
    void span(int from, int to, Cost cost = 1) {
        path[from].push_back(Edge<Cost>(from, to, cost));
    }
    std::vector<Edge<Cost>>& operator[](int v) { return path[v]; }
};

int main() {
    int N;
    std::cin >> N;

    Graph<> graph(N);
    for (int i = 0; i < N - 1; ++i) {
        int x, y;
        std::cin >> x >> y;
        --x, --y;
        graph.span(x, y);
        graph.span(y, x);
    }

    std::function<int(int, int)> dfs = [&](int v, int r) {
        int ret = 0;
        for (auto e : graph[v]) {
            if (e.to == r) continue;
            ret ^= (dfs(e.to, v) + 1);
        }
        return ret;
    };

    std::cout << (dfs(0, -1) > 0 ? "Alice" : "Bob") << std::endl;
    return 0;
}

