int DP[100005];
vector <int> G[100005];
int N;
void Read()
{
    cin >> N;
    for(int i = 1; i < N; i++)
    {
        int x, y;
        cin >> x >> y;
        G[x].push_back(y);
        G[y].push_back(x);
    }
}

void DFS(int node, int father)
{
    vector <int> V;
    V.clear();
    for(int i = 0; i < G[node].size(); i++)
    {
        int neighb = G[node][i];
        if(neighb == father)
            continue;
        DFS(neighb, node);
        V.push_back(DP[neighb]);
    }
    V.push_back(0);
    sort(V.begin(), V.end());
    int val = -1;
    for(int i = 0; i < V.size() - 1; i++)
    {
        if(V[i + 1] > V[i] + 1)
        {
            val = V[i] + 1;
            break;
        }
    }
    if(val == -1)
        val = V.back() + 1;
    DP[node] = val;
}
int main()
{
    Read();
    DFS(1, 0);
    int S = 0;
    for(int i = 0; i < G[1].size(); i++)
    {
        S = (S ^ DP[G[1][i]]);
    }
    if(S == 0)
    {
        cout << "Bob\n";
    }
    else
        cout << "Alice\n";
    return 0;
}

#include <bits/stdc++.h>

using namespace std;

vector<vector<int>> adjlist;
vector<bool> visited;

int visit(int v) {
  visited[v] = true;
  vector<int> unvisited;
  for (int w : adjlist[v]) {
    if (!visited[w]) {
      unvisited.push_back(w);
    }
  }

  if (unvisited.size() == 0) return 0;
  else if (unvisited.size() == 1) {
    return 1 + visit(unvisited[0]);
  } else {
    int res = 0;
    for (int w : unvisited) {
      int test = visit(w);
      res ^= test;
    }
    return res;
  }
}

int main() {
  int n;
  scanf("%d", &n);
  adjlist.resize(n);
  for (int i = 0; i < n - 1; i++) {
    int a, b;
    scanf("%d %d", &a, &b);
    a--;
    b--;
    adjlist[a].push_back(b);
    adjlist[b].push_back(a);
  }

  visited.assign(n, false);
  int test = visit(0);
  if (test == 0) printf("Bob\n");
  else printf("Alice\n");
  return 0;
}

