int dp[100005],N;
vector<int> G[100005];
void dfs(int nod,int tata)
{
    int cate=0,val=1;
    for(auto it:G[nod])
    {
        int tmp;
        if(it==tata)continue;
        dfs(it,nod);tmp=dp[it];
        cate+=tmp;
        val^=(!tmp);
    }
    if(!cate)dp[nod]=(!val);
    else dp[nod]=(cate%2==1);
}
int main()
{
    cin>>N;
