ll const MAX = 100005;
ll gr[MAX];
vector<ll> children[MAX];
vector<ll> G[MAX];
ll parent[MAX];

void grundy(ll x) {
	ll ans = 0;
	for (ll i = 0; i < children[x].size(); i++) {
		ll c = children[x][i];
		ll cnt = 1 + gr[c];
		ans ^= cnt;
	}
	gr[x] = ans;
}

int main() {
	ll n;
	cin >> n;
