		G[y].push_back(x);
	}
	stack<ll> st1,st2;
	st1.push(1); st2.push(1);
	parent[1] = 1;
	while (!st1.empty()) {
		ll cur = st1.top();
		st1.pop();
		for (ll i = 0; i < G[cur].size(); i++) {
			ll next = G[cur][i];
			if (next != parent[cur]) {
				children[cur].push_back(next);
				parent[next] = cur;
				st1.push(next);
				st2.push(next);
			}
		}
	}
	while (!st2.empty()) {
		ll cur = st2.top();
		st2.pop();
		grundy(cur);
	}
	if (gr[1] == 0) {
		cout << "Bob" << endl;
	}
	else {
		cout << "Alice" << endl;
	}
}

