using namespace std;

vector<vector<int>> adjlist;
vector<bool> visited;

int visit(int v) {
  visited[v] = true;
  vector<int> unvisited;
  for (int w : adjlist[v]) {
    if (!visited[w]) {
      unvisited.push_back(w);
    }
  }

  if (unvisited.size() == 0) return 0;
  else if (unvisited.size() == 1) {
    return 1 + visit(unvisited[0]);
  } else {
    int res = 0;
    for (int w : unvisited) {
      int test = 1 + visit(w);
      res ^= test;
    }
    return res;
  }
}

