    a--;
    b--;
    adjlist[a].push_back(b);
    adjlist[b].push_back(a);
  }

  visited.assign(n, false);
  int test = visit(0);
  if (test == 0) printf("Bob\n");
  else printf("Alice\n");
  return 0;
}

