vector<ll> par;
vector<ll> treeRank;
void makeRootedTree(vector<ll> &parentVector, vector<ll> &treeRankVector, ll vecSize, ll root) {
    parentVector = vector<ll>(vecSize);
    treeRankVector = vector<ll>(vecSize);
    assert(root < (ll)parentVector.size());
    parentVector[root] = -1;
    treeRankVector[root] = 0;
    queue<ll> que;
    que.push(root);
    while (!que.empty()) {
        ll node = que.front();
        que.pop();
        REP (i, 0, G[node].size()) {
            edge e = G[node][i];
            if (e.to != parentVector[node]) {
                parentVector[e.to] = node;
                treeRankVector[e.to] = treeRankVector[node] + 1;
                que.push(e.to);
            }
        }
    }
}

vector<ll> grundy;
//
void leafToRoot(vector<ll> &parentVector, ll root) {
    queue<ll> que;
    vector<ll> used(parentVector.size());
    fill(used.begin(), used.end(), 0);
    REP(i, 0, parentVector.size()) {
        if (i != root && G[i].size() == 1) {
            que.push(i);
            used[i] = 1;
        }
    }
    grundy = vector<ll>(parentVector.size(), 0);
    while (!que.empty()) {
        ll node = que.front();
        que.pop();

        //root

        if (node == root) {
            break;
        }
        ll parent = parentVector[node];
        used[parent]++;

        //
        grundy[parent] ^= grundy[node] + 1;

        if ((parent == root && used[parent] == (ll)G[parent].size()) ||
            (parent != root && used[parent] == (ll)G[parent].size() - 1)) {
            que.push(parent);
        }
    }
}

