int grundy(int n, int pre) {
	//print(n);
	if (pre == -1) {
		if (G[n].size() == 1) {
			return 1;
		}
		else {
			int res = 0;
			for (auto nex : G[n]) {
				res ^= grundy(nex, n);
			}
			return res;
		}
	}
	else {
		if (G[n].size() == 2) {
			int res = 0;
			for (auto nex : G[n]) {
				if (nex == pre)continue;
				res = grundy(nex, n);
			}
			res++;
			return res;
		}
		else {
			int res = 0;
			for (auto nex : G[n]) {
				if (nex == pre)continue;
				res ^= grundy(nex, n);
			}
			return res;
		}
	}
}


