			seg[v] = seg[v * 2 + 1] + seg[v * 2 + 2];
			//
		}
	}
	T query(ll a, ll b, ll k, ll l, ll r) {	 //[a,b)
		if(r <= a || b <= l) return 0;
		if(a <= l && r <= b) return seg[k];
		// cout << "#" << a << " " << b << " " << k << " " << l << " " << r << endl;
		T vl = query(a, b, k * 2 + 1, l, (l + r) / 2);
		T vr = query(a, b, k * 2 + 2, (l + r) / 2, r);

		return vl + vr;	 //
