using namespace std;

struct SegtreeMin {
    int n;
    long long inf;
    vector<long long> dat;

    void initialize(int n_input, long long inf_input){
        inf = inf_input;
        n = 1;
        while(n < n_input)n <<= 1;
        dat.resize(2*n-1);
        for(int i = 0; i<2*n-1; i++) dat[i] = inf;
    }

    void update(int k, long long a){
        k += n - 1;
        dat[k] = a;
        while(k > 0){
            k = (k - 1)/2;
            dat[k] = min(dat[2*k+1], dat[2*k+2]);
        }
    }

    long long min_between(int a, int b){
        return query(a, b+1, 0, 0, n);
    }

    long long query(int a, int b, int k, int l, int r){
        if(r<=a || b<=l)return inf;
        if(a<=l && r<=b)return dat[k];
        long long vl = query(a, b, 2*k+1, l, (l+r)/2);
        long long vr = query(a, b, 2*k+2, (l+r)/2, r);
        return min(vl, vr);
    }
};


int N;
const int MAX = 100000;
vector<int> edges[MAX];
int grundy[MAX];
int gnum[MAX];
SegtreeMin st;

int dfs(int i, int prev){
    int gr = st.min_between(0, N-1);
    gnum[gr]++;
    st.update(gr, MAX);

    int ret = gr;
    for(auto j : edges[i]){
        if(j == prev) continue;
        ret ^= dfs(j, i);
    }

    gnum[gr]--;
    if(gnum[gr] == 0) st.update(gr, gr);
    return ret;
}

int main(){
    int i, j, k;
    cin >> N;
    for(i=0; i<N-1; i++){
        int x, y;
        cin >> x >> y;
        edges[x-1].push_back(y-1);
        edges[y-1].push_back(x-1);
    }
    st.initialize(N, MAX);
    for(i=0; i<N; i++) st.update(i, i);

    int gr = dfs(0, -1);
    cout << (gr!=0 ? "Alice" : "Bob") << endl;
    return 0;
}

