int main(){
	//freopen("xxx.in","r",stdin);
	splay(n);
	for(int i=1;i<n;i++){
		int x,y;splay(x),splay(y);
		add(x,y),add(y,x);
	}
	int l=0,r=1;q[1]=1;
	while(l!=r){
