struct Edge
{
    int nxt, to;
} eg[N << 1];
int head[N], en;

void setEdge(int u, int v)
{
    eg[++en] = (Edge){head[u], v};
    head[u] = en;
}

void dfs(int u, int fa)
{
    for (int e = head[u]; e; e = eg[e].nxt)
    {
        int v = eg[e].to;
        if (v == fa) continue;
        sg[u] ^= (sg[v] + 1);
    }
}

