			Edge *temp=(*i1);
			if(temp==e1||temp==e2||temp->to->count_finish){
				continue;
			}
			retval+=temp->to->count_union(e1,e2);
		}
		return retval;
	}
	int64_t game(){
		searched=true;
		int64_t size=0;
		int64_t xor_num=0;
		for(auto i1=departure.begin();i1!=departure.end();i1++){
			Edge *temp=(*i1);
			if(!temp->to->searched){
				size++;
				int64_t c_ans=temp->to->game();
				xor_num=xor_num^c_ans;
			}
		}
		if(size==0){
			return 1;
		}else if(size==1){
			return xor_num+1;
		}else if(xor_num==0){
			return 1;
		}else{
			return 2;
		}
	}
};
