  ios::sync_with_stdio(false);

  cin >> N;

  int root = 0;
  int edge = 0;

  rep(i, N - 1) {
      cin >> x[i] >> y[i];
      x[i]--; y[i]--;
      if (x[i] > y[i]) swap(x[i], y[i]);
