        root -= 1;
        cand.erase(y[i]);
    }
  }

  int hand = 0;
  while (true) {
    if (edge == 0) {
        root -= 1;
    }
    else {
        if (root%2 == 1) {
            edge -= 1;
        } else {
            root -= 1;
        }
    }
    if (root == 0) {
      cout << (hand == 1 ? "Bob":"Alice") << endl;
      break;
    }

