void DFS(int node, int father)
{

    for(int i = 0; i < G[node].size(); i++)
    {
        int neighb = G[node][i];
        if(neighb == father)
            continue;
        DFS(neighb, node);

    }
    V.clear();
    for(int i = 0; i < G[node].size(); i++)
    {
        int neighb = G[node][i];
        if(neighb == father)
            continue;
        V.push_back(DP[neighb]);
    }
    V.push_back(0);
    sort(V.begin(), V.end());
    int val = -1;
    for(int i = 0; i < V.size() - 1; i++)
    {
        if(V[i + 1] > V[i] + 1)
        {
            val = V[i] + 1;
            break;
        }
    }
    if(val == -1)
        val = V.back() + 1;
    DP[node] = val;
}
