template<class T=int>class segdi : public digraph<T>{int getid(int k){        if(k >= len*2-1){            return k;        }        else if(k < len - 1){            return k + len;        }else{            return k - len + 1;        }    }void add(int f, int t, T c = 1) {        f = getid(f);        t = getid(t);        if (!(0 <= f && f < n && 0 <= t && t < n)) {            debugline("segdi add");            deb(f, t, c);            ole();        }        g[f].emplace_back(f, t, c);        /*digraphedges*/    }int mid;int len;
public:
    using digraph<T>::g;    using digraph<T>::n;    using digraph<T>::edges;
    //resize
    segdi(int n_) : digraph<T>(1) {        int nn = 1;        while (nn < n_)nn *= 2;        n_ = nn;        len = n_;        this->resize(n_ + (n_ - 1) * 2 + n_);        mid = n_ + (n_ - 1) * 2;        int ad = len * 2 - 1;        rep(i, len - 1) {            add(i, i * 2 + 1, 0);            add(i, i * 2 + 2, 0);            if (i * 2 + 1 >= len - 1) {                add(i * 2 + 1, i + ad, 0);                add(i * 2 + 2, i + ad, 0);            } else {                add(i * 2 + 1 + ad, i + ad, 0);                add(i * 2 + 2 + ad, i + ad, 0);            }        }    }
    void dfs(vi &list, int nl, int nr, int k, int l, int r) {        if (r <= nl || nr <= l)return;        if (l <= nl && nr <= r) {            list += k;        } else {            dfs(list, nl, (nl + nr) / 2, k * 2 + 1, l, r);            dfs(list, (nl + nr) / 2, nr, k * 2 + 2, l, r);        }    }
    void rekkyo(vi &list, int l, int r) {        l += len - 1;        r += len - 1;        while (l < r) {            if (!(l & 1)) { list.push_back(l); }            if (!(r & 1)) { list.push_back(r - 1); }            l >>= 1;            r = (r - 1) >> 1;        }    }
    //
    void add(int fl, int fr, int tl, int tr, int cost) {        /*f*/        int ad = 2 * len - 1;        if (mid >= n) { this->resize(n + 100); }        {            int l = fl, r = fr;            l += len - 1;            r += len - 1;            while (l < r) {                if (!(l & 1)) {                    if (l - len + 1 < 0)add(l + ad, mid, cost);                    else add(l, mid, cost);                }                if (!(r & 1)) {                    if (r - 1 - len + 1 < 0)add(r - 1 + ad, mid, cost);                    else add(r - 1, mid, cost);                }                l >>= 1;                r = (r - 1) >> 1;            }        }        {            int l = tl, r = tr;            l += len - 1;            r += len - 1;            while (l < r) {                if (!(l & 1)) { add(mid, l, 0); }                if (!(r & 1)) { add(mid, r - 1, 0); }                l >>= 1;                r = (r - 1) >> 1;            }        }        mid++;    }
};
