template<class T> int krus(undigraph<T> &g) {    int res = 0;    unionfind uf(g.n);    if (sz(g.edges) == 0)g.set_edges();    int i = 0;    auto E = g.edges;    sort(E);    fora(e, E) { if (uf.unite(e.f, e.t)) { res += e.c; }}    return res;}
