tree<int> next_tree(int k) {    assert(2 <= k && k < 11);    static str name;    static ifstream ina;    static int rem;    static vp edges;    static int pk = -1;/*k*/    if (pk != k) {        if (~pk)ina.close();        edges.clear();        pk = k;        name = (k == 6) ? "C:\\Users\\kaout\\Desktop\\trees_sizek\\nazeka6.txt" : "C:\\Users\\kaout\\Desktop\\trees_sizek\\tree_size" + tos(k) + ".txt";        ina = ifstream(name);        rem = pow(k, k - 2);/*Cayley*/        rep(i, k)rep(j, i + 1, k)edges.emplace_back(i, j);        pk = k;    }    tree<int> g(k);    if (rem == 0) {        g.resize(0);        return g;    }    int m;    ina >> m;    while (m) {        int lb = lbit(m);        int id = log2(lb);        g.add(edges[id].first, edges[id].second);        m ^= lb;    }    rem--;    return g;}
undigraph<int> next_undi(int k) {    assert(2 <= k && k < 9);    static str name;    static ifstream ina;    static int rem;    static vp edges;    static vi lims = {-1, -1, 1, 4, 38, 728, 26704, 1866256};    static int pk = -1;/*k*/    if (pk != k) {        if (~pk)ina.close();        edges.clear();        pk = k;        name = (k == 6) ? "C:\\Users\\kaout\\Desktop\\undi_sizek\\roku.txt" : "C:\\Users\\kaout\\Desktop\\undi_sizek\\undi_size" + tos(k) + ".txt";        ina = ifstream(name);        rem = lims[k];        rep(i, k)rep(j, i + 1, k)edges.emplace_back(i, j);        pk = k;    }    undigraph<int> g(k);    if (rem == 0) {        g.resize(0);        return g;    }    int m;    ina >> m;    while (m) {        int lb = lbit(m);        int id = log2(lb);        g.add(edges[id].first, edges[id].second);        m ^= lb;    }    rem--;    return g;}
vector<tree<int>> trees(int k) {    vector<tree<int>> res;    while (1) {        tree<int> g = next_tree(k);        if (sz(g) == 0)break;        res.push_back(g);    }    return res;}
