int mexx(vector<int> cur) {
    if (cur.empty()) return 0;
    sort(cur.begin(), cur.end());
    if (cur.front() != 0) {
        return 0;
    }
    for (int i = 0; i + 1 < (int)cur.size(); i++) {
        if (cur[i] + 1 < cur[i + 1]) {
            return cur[i] + 1;
        }
    }
    return cur.back() + 1;
}

void dfs(int cur) {
    used[cur] = 1;
    vector<int> sons;
    for (auto t : g[cur]) {
