            dfs(t);
            sons.push_back(t);
        }
    }
    if (sons.empty()) {
        gr[cur] = 0;
    } else {
        for (auto t : sons) {
            gr[cur] ^= gr[t];
        }
        if (sons.size() == 1) {
            gr[cur]++;
        }
    }
}

