  }
  queue<int> q;
  int par[n+1];
  par[1]=-1;
  q.push(1);
  bool used[n+1];
  fill(used,used+n+1,false);
  while(q.size()>0){
    int now=q.front();q.pop();
    rep(i,0,v[now].size()){
      int ne=v[now][i];
