const int N = 123456;

int n;
vector<int> e[N];

int grundy(int u, int p = -1) {
    int deg = 0;
    for (int v: e[u]) {
        if (v == p) continue;
        deg++;
    }

    int g = 0;
    for (int v: e[u]) {
        if (v == p) continue;
        if (deg == 1) {
            return grundy(v, u) + 1;
        } else {
            g ^= grundy(v, u);
        }
    }
    return g;
}

