const int MAXN = 1e5 + 100;

int n;
vector<int> adj[MAXN];
LL g[MAXN];

void go(int r, int p) {
	g[r] = 0;
	int cnt = 0;
	for(auto &elem: adj[r]) {
		if(elem != p) {
			go(elem, r);
			cnt++;
		}
	}
	if(cnt == 0) {
		g[r] = 0;
	} else if(cnt == 1) {
		for(auto &elem: adj[r]) {
			if(elem != p) {
				g[r] = g[elem] + 1;
			}
		}
	} else {
		for(auto &elem: adj[r]) {
			if(elem != p) {
				g[r] = g[r] ^ g[elem];
			}
		}
	}
}

