vector<vector<int> > G, T;

void build(int a, int p) {
	for (int i = 0;i < G[a].size();i++) {
		if (G[a][i] == p) continue;
		T[a].push_back(G[a][i]);
		build(G[a][i], a);
	}
	return;
}

int cal(int x) {
	int nim = 0;
	for (int i = 0;i < T[x].size();i++) nim ^= cal(T[x][i]);
	nim++;
    return nim;
}

int main() {
