vector<int> adjl[100005];

int func(int node,int parent){
    int children = 0;
    for (int x : adjl[node]){
        if (x==parent) continue;
        children++;
    }
    if (children%2==1) return 1;
    else{
        int sum = 0;
        for (int x : adjl[node]){
            if (x==parent) continue;
            sum += func(x,node);
        }
        return sum%2;
    }
}

