bool winner(vector<int> index){
    int counter =0;
    vector<int> nextindex;
    if(index.size()==0)return false;
    for(int i=0;i<index.size();i++){
        int k=index[i];
        used[k]=true;
        counter+=cnt[k];
        for(int j=0;j<tree[k].size();j++){
            if(!used[tree[k][j]]){
                nextindex.push_back(tree[k][j]);
            }
        }
    }

    if(counter%2==1){
        return true;
    }else{
        return winner(nextindex);
    }

}



