ll n;
ll par[N];
vector<ll> g[N];
ll d[N];

void dfs(ll u, ll p) {
    vector<ll> v;

    rep(i,0,g[u].size()) {
        ll ch = g[u][i];
        if (ch == p) continue;
        dfs(ch,u);
        v.push_back(d[ch]);
    }

    if (v.size() == 1)
        d[u] = v[0];
    else if (v.size() > 1) {
        ll mask = 0;
        rep(i,0,v.size()) {
            mask ^= v[i];
        }
        d[u] = !!mask;
    }
    d[u] += 1;
}

void solve() {
    cin >> n;
    rep(i,0,n-1) {
        ll a, b; cin >> a >> b; a--,b--;
        g[a].push_back(b);
        g[b].push_back(a);
    }
    dfs(0,0);
    if (d[0] != 1) {
        cout << "Alice" << endl;
    }
    else {
        cout << "Bob" << endl;
    }
}


int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);
    solve();
}

