const int MAX_N = 100000;

int N;
vector<int> G[MAX_N];

int calc_grundy(int v = 0, int par = -1) {
    int grundy = 0;

    for (int to : G[v]) {
        if (to == par) {
            continue;
        }
        grundy ^= (calc_grundy(to, v) + 1);
    }

    return grundy;
}

