    int x, y;

    cin >> N;
    for (int i = 0; i < N - 1; i++) {
        cin >> x >> y; x--; y--;
        G[x].push_back(y);
        G[y].push_back(x);
    }

    cout << (calc_grundy() != 0 ? "Alice" : "Bob") << endl;

    return 0;

}

