struct Problem{
    int n,cnt,b;
    vector<vector<int>> t;
    vector<int> vis,dep;
    set<int> od;
    Problem(LL n):n(n),cnt(0),t(n),vis(n),dep(n){};
    void dfs(int cur){
        od.erase(cur);
        if((int)t[cur].size()==1 and cur!=0){
            return;
        }
        for(auto x:t[cur]){
            if(dep[x]>dep[cur]){
                dfs(x);
            }
        }
    }
    void solve(){
        for(int i=0; i<n-1; ++i){
            int a,b;
            cin >> a >>b;
            --a;--b;
            t[a].push_back(b);
            t[b].push_back(a);
        }
        queue<int> Q;
        Q.push(0);
        while(!Q.empty()){
            int tmp=Q.front();
            vis[tmp]=1;
            Q.pop();
            if(((int)t[tmp].size()-(tmp!=0))%2==1){
                od.insert(tmp);
                if(od.size()==1) b=tmp;
            }
            for(auto x:t[tmp]){
                if(vis[x]==0){
                    Q.push(x);
                    dep[x]=dep[tmp]+1;
                }
            }
        }
        if(od.empty()){
            cout << "Bob"<<"\n";
            return;
        }
        DBG(od)
        od.erase(b);
        int aa=od.size();
        for(auto y:t[b]){
            if(dep[y]>dep[b]){
                dfs(y);
                DBG(od)
                if(od.size()<aa and od.size()>0){
                    cout << "Bob"<<"\n";
                    return ;
                }else if(od.empty()){
                    cout << "Alice"<<"\n";
                    return ;
                }
            }
        }
        if(od.empty())cout << "Alice"<<"\n";
        else cout << "Bob"<<"\n";
    }
};

int main(){
    cin.tie(0);
    ios_base::sync_with_stdio(false);
    long long n=0;
    cin >> n;
    Problem p(n);
    p.solve();
    return 0;
}

