vector<int> tree[100000];

int grundy(int x, int prev) {
    int g = 0;
    for (int v : tree[x]) {
        if (v != prev)
            g ^= grundy(v, x);
    }
    return g + 1;
}

int main(){
    int N;
    scanf("%d", &N);

    for (int i = 0; i < N - 1; ++i) {
        int v1, v2;
        scanf("%d%d", &v1, &v2);
        --v1;
        --v2;
        tree[v1].push_back(v2);
        tree[v2].push_back(v1);
    }

    printf(grundy(0, -1) == 1 ? "Bob\n" : "Alice\n");
}

