	}
	queue<int>Q;
	for (auto i : edge[1])Q.push(i);
	int dis[100001] = {};
	bool used[100001] = {};
	used[1] = true;
	while (!Q.empty()) {
		int c = Q.front();
		if (used[c]) {
			Q.pop();
			continue;
		}
		if (edge[c].size() > 3)dis[c] = 1;
		used[c] = true;
		bool add = true;
		for (auto i : edge[c]) {
			if (!used[i]) {
				add = false;
				dis[i] = dis[c] + 1;
				Q.push(i);
			}
		}
		if (add)ans ^= dis[c];
		Q.pop();
	}
	if (ans)cout << "Alice\n";
	else cout << "Bob\n";
	return 0;
}

