int nim(int x)
{
    if (children[x].size() == 0)
        return 0;
    else if (children[x].size() == 1)
        return nim(*children[x].begin())+1;
    else
    {
        int nm = 0;
        for (std::vector<int>::iterator it = children[x].begin(); it != children[x].end(); it++)
            nm ^= nim(*it)+1;
        return nm;
    }
}

