#include <bits/stdc++.h>

using namespace std;

const int N = 100010;

int head[N], num, i, n, x, y;
struct edge {
	int to, nxt;
} e[N * 2];

int rd() {
	int x = 0; char c = getchar();
	while (c > '9' || c < '0') c = getchar();
	while (c >= '0' && c <= '9') x = x * 10 + c - 48, c = getchar();
	return x;
}

void add_edge(int x, int y) {
	e[++num].nxt = head[x], e[head[x] = num].to = y;
}

int sg(int x, int p) {
	int now = 0, i, y;
	for (i = head[x]; i; i = e[i].nxt) {
		y = e[i].to;
		if (y == p) continue;
		now ^= (sg(y, x) + 1);
	}
	return now;
}

int main() {
	n = rd();
	for (i = 1; i < n; i++) {
		x = rd(), y = rd();
		add_edge(x, y), add_edge(y, x);
	}
	if (sg(1, 1)) puts("Alice"); else puts("Bob");
	return 0;
}

