const int max_N = 1e5 + 10;
vector<int> E[max_N];
int spgn[max_N], vis[max_N];

void dfs(int node);

int main()
{
	int N;
	cin >> N;
	for (int i = 1; i < N; ++i) {
		int x, y;
		cin >> x >> y;
		E[x].push_back(y);
		E[y].push_back(x);
	}

	dfs(1);
	if (spgn[1]) {
		cout << "Alice" << endl;
	} else {
		cout << "Bob" << endl;
	}
}

void dfs(int node)
{
	vis[node] = 1;
	int cnt = 0;
	int temp = 0;
	for (auto &v : E[node]) {
		if (!vis[v]) {
			dfs(v);
			temp ^= spgn[v];
			++cnt;
		}
	}
	if (cnt > 1) {
		spgn[node] = temp;
	} else if (cnt == 1) {
		spgn[node] = temp + 1;
	} else {
		spgn[node] = 0;
	}
}

