int last[MAXN + 5], other[MAXN + 5], pre[MAXN + 5];

int N, all = -1;

void build(int u, int v)
{
	pre[++all] = last[u];
	last[u] = all;
	other[all] = v;
}

int dfs(int ans, int fa)
{
	int cnt = 0;
	int dt = last[ans];
	while(dt != -1){
		int dr = other[dt];
		if(dr != fa) cnt ^= dfs(dr, ans);
		dt = pre[dt];
	}
	if(ans == 1) return cnt;
	return cnt + 1;
}

