vector<int> t_parent;
vi t_ord;

void tree_getorder(const vector<vi> &g, int root) {
	int n = g.size();
	t_parent.assign(n, -1);
	t_ord.clear();

	vector<int> stk; stk.push_back(root);
	while (!stk.empty()) {
		int i = stk.back(); stk.pop_back();
		t_ord.push_back(i);
		for (int j = (int)g[i].size() - 1; j >= 0; j--) {
			int c = g[i][j];
			if (t_parent[c] == -1 && c != root)
				stk.push_back(c);
			else
				t_parent[i] = c;
		}
	}
}

int main() {
		int N;
		scanf("%d", &N);
		vector<vector<int> > g(N);
		for (int i = 0; i < N - 1; ++i) {
			int u, v;
			scanf("%d%d", &u, &v), --u, --v;
			g[u].push_back(v);
			g[v].push_back(u);
		}
		tree_getorder(g, 0);
		vector<int> grundy(N);
		for (int ix = (int)t_ord.size() - 1; ix > 0; --ix) {
			int i = t_ord[ix], p = t_parent[i];
			grundy[p] ^= grundy[i] + 1;
		}
		bool ans = grundy[0] != 0;
		puts(ans ? "Alice" : "Bob");
	return 0;
}

