const int N = 1e5 + 7;

int gr[N];
int used[N];
vector<int> g[N];

int mexx(vector<int> cur) {
    sort(cur.begin(), cur.end());
    if (cur.front() != 0) {
        return 0;
    }
    for (int i = 0; i + 1 < (int)cur.size(); i++) {
        if (cur[i] + 1 < cur[i + 1]) {
            return cur[i] + 1;
        }
    }
    return cur.back() + 1;
}

void dfs(int cur) {
    used[cur] = 1;
    vector<int> sons;
    for (auto t : g[cur]) {
        if (!used[t]) {
            dfs(t);
            sons.push_back(t);
        }
    }
    if (sons.empty()) {
        gr[cur] = 1;
    } else {
        vector<int> tomex;
        {
            int cx = 0;
            for (auto t : sons) {
                cx ^= gr[t];
            }
            // tomex.push_back(cx);
            if (sons.size() > 1) {
                for (auto t : sons) {
                    tomex.push_back(cx ^ gr[t]);
                }
            }
        }
        gr[cur] = mexx(tomex);
    }
}

