  static int solve(Node a, int from) {
    if (a.adj.size()==1 && a.i!=0) {
      return 0;
    }
    int ans = 0;
    for (Node n:a.adj) {
      if (n.i!=from) {
        ans ^= 1+solve(n,a.i);
      }
    }
    //System.out.println("Answer for "+a.i+": "+ans);
    return ans;
  }
