    }

    public static void solve(ExtendedScanner sc, Out out) {
        int n = sc.nextInt();
        var builder = new TreeBuilder(n);
        for (int i = 1; i < n; i++) {
            int u = sc.nextInt() - 1;
            int v = sc.nextInt() - 1;
            builder.addEdge(u, v);
        }
        var t = builder.build();
        int[] p = t.parent();
        int[] ord = Trees.postorder(t);
        int[] grundy = new int[n];
        for (int u : ord) {
            t.getEdges(u).iterator().forEachRemaining((int v) -> {
                if (v != p[u]) {
                    grundy[u] ^= grundy[v] + 1;
                }
            });;
        }
        out.writeln(grundy[0] == 0 ? "Bob" : "Alice");
    }
}


