public class Main {
    public static void main(String[] args) {
        StringBuilder out = new StringBuilder();
        solve(out);
        PrintWriter pw = new PrintWriter(System.out);
        pw.println(out);
        pw.flush();
        pw.close();
    }

    public static void solve(StringBuilder out) {
        int n = In.ni();
        ArrayList<IntArrayList> g = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            g.add(new IntArrayList());
        }
        for (int i = 0; i < n - 1; i++) {
            int x = In.ni() - 1;
            int y = In.ni() - 1;
            g.get(x).add(y);
            g.get(y).add(x);
        }
        int[] cnt = new int[n];
        int[] par = new int[n];
        int[] dep = new int[n];
        IntQueue q = new IntQueue(n);
        q.add(0);
        while (q.size() > 0) {
            int u = q.poll();
            cnt[dep[u]]++;
            g.get(u).iterator().forEachRemaining((int v) -> {
                if (v != par[u]) {
                    par[v] = u;
                    dep[v] = dep[u] + 1;
                    q.add(v);
                }
            });
        }
        for (int i = 1; i < n; i++) {
            if ((cnt[i] & 1) != 0) {
                out.append("Alice");
                return;
            }
        }
        out.append("Bob");
    }
