        }
        int g = dfs(0, -1);
        out.println(g != 0 ? "Alice" : "Bob");
    }

    int dfs(int v, int p){
        int g = 0;
        for(int child : G.get(v)){
            if(child == p) continue;
            g ^= (dfs(child, v) + 1);
        }
        return g;
    }
