class Program
{
    static int N;
    static List<int>[] E;

    static void Main()
    {
        D();
    }

    static void D()
    {
        N = int.Parse(Console.ReadLine());
        E = new List<int>[N];
        for (int i = 0; i < N; i++)
        {
            E[i] = new List<int>();
        }
        for (long i = 0; i < N - 1; i++)
        {
            var XY = Console.ReadLine().Split(' ');
            E[int.Parse(XY[0]) - 1].Add(int.Parse(XY[1]) - 1);
        }

        Console.WriteLine(Grundy(0) == 0 ? "Bob" : "Alice");
    }

    static int Grundy(long root)
    {
        var s = new Stack<int>();

        foreach (var n in E[root])
        {
            s.Push(Grundy(n));
        }

        if (s.Count() == 0)
        {
            return 0;
        }
        else if (s.Count() == 1)
        {
            return s.Pop() + 1;
        }

        int ans = 0;
        while (s.Count > 0)
        {
            ans ^= s.Pop();
        }
        return ans;
    }
}

