using System;
using System.Collections.Generic;
using System.Text;

public class Program
{
    private int N;
    private List<int>[] Edge;

    public void Solve()
    {
        var sc = new Scanner();
        N = sc.NextInt();
        Edge = new List<int>[N];
        for (int i = 0; i < N; i++)
        {
            Edge[i] = new List<int>();
        }

        for (int i = 0; i < N - 1; i++)
        {
            int x = sc.NextInt() - 1;
            int y = sc.NextInt() - 1;
            Edge[x].Add(y);
            Edge[y].Add(x);
        }

        Console.WriteLine(Win(0,-1) > 0?"Alice":"Bob");
    }

    private int Win(int pos, int parent)
    {
        int result = 0;
        foreach (int i in Edge[pos])
        {
            if (i == parent) continue;
            result ^= 1+Win(i, pos);
        }

        return result;
    }

    public static void Main(string[] args)
    {
        new Program().Solve();
    }
}

