            E[XY.ElementAt(0)].Add(XY.ElementAt(1));
            E[XY.ElementAt(1)].Add(XY.ElementAt(0));
        }

        Console.WriteLine(Grundy(0, 0) == 0 ? "Bob" : "Alice");
    }

    static int Grundy(int root, int prev)
    {
