using System.Collections.Generic;
using System.Linq;
using System;

class Program
{
    static long N;
    static bool[,] E;
    static bool[] visited;

    static void Main()
    {
        D();
    }

    static void D()
    {
        N = long.Parse(Console.ReadLine());
        E = new bool[N, N];
        visited = new bool[N];
        for (long i = 0; i < N - 1; i++)
        {
            var XY = Console.ReadLine().Split(' ').Select(x => long.Parse(x) - 1);
            E[XY.ElementAt(0), XY.ElementAt(1)] = true;
            E[XY.ElementAt(1), XY.ElementAt(0)] = true;
        }

        Console.WriteLine(Grundy(1) == 0 ? "Bob" : "Alice");
    }

    static long Grundy(long root)
    {
        visited[root - 1] = true;
        var s = new Stack<long>();

        foreach (var n in Next(root))
        {
            if (!visited[n - 1])
            {
                s.Push(Grundy(n));
            }
        }
        if (s.Count() == 0) return 0;
        else if (s.Count() == 1) return s.Pop() + 1;

        long ans = 0;
        while (s.Count > 0)
        {
            ans ^= s.Pop();
        }
        return ans;
    }

    static IEnumerable<long> Next(long root)
    {
        for (int i = 0; i < N; i++)
        {
            if (E[root - 1, i] && !visited[i]) yield return i + 1;
        }
    }
}

